/// <reference path="../../typings/index.d.ts" />
"use strict";
//  create the editor instance, passing in options

var instance = Kwill.createKwillEditor({
    config: {
        debug: true
    },
    selector: '.editable-text--first',
    interaction: {
        show_popup_on_click: false,
        expand_x: false,
        expand_y: false
    },
    content: {}
});

Kwill.createKwillTextFormatUi({
    popupSelector: '.kwill-text-format-popup',
    fonts: { google: ['Roboto'] }
});

// Kwill.createKwillTextFormatUi({
//     popupSelector: '.kwill-text-format-popup'
// });

Kwill.getSignal('edit progress').add(function() { console.log('edit progress') });
Kwill.getSignal('edit complete').add(function() { console.log('edit complete') });