import * as log from 'loglevel';
import {getTextEditor, TextEditor} from "./TextEditor";
import {IKwillInstanceOptions} from "./createKwillEditor";

/**
 * get the options object for an editor instance
 * @param instanceID
 */
export function getEditorOptions(instanceID: string) : IKwillInstanceOptions
{
    return getTextEditor(instanceID).options;
}


/**
 * get the element for an editor instance
 * @param instanceID
 */
export function getEditorElement(instanceID: string) : HTMLElement
{
    return getTextEditor(instanceID).element;
}


/**
 * set the content of an editor instance
 * @param instanceID
 */
export function getTextEditorContent(instanceID: string) : string
{
    //  get an editor instance by ID

    const editorInstance: TextEditor = getTextEditor(instanceID);

    //  if the instance exists, set its content

    if (editorInstance) return editorInstance.content;
}


/**
 * set the content of an editor instance
 * @param instanceID
 * @param content
 */
export function setTextEditorContent(instanceID: string, content: string)
{
    //  get an editor instance by ID

    const editorInstance: TextEditor = getTextEditor(instanceID);

    //  if the instance exists, set its content

    if (editorInstance) editorInstance.content = content;
}