import {removeGlobalListeners} from "../globals/GlobalInteractions";
import {destroyAllGlobalSignals} from "../events/GlobalEvents";
import {destroyAllTextEditors} from "./TextEditor";
import {destroyAllTextFormatUi} from "../text-format-ui/TextFormatUi";
import {destroyOneTimeSetup} from "../globals/OneTimeSetup";


/**
 * destroy all active instances
 * @param forever
 */
export function destroyAllKwillInstances(forever?: boolean) : void
{
    //  remove global listeners

    removeGlobalListeners();

    //  destroy all global signals

    destroyAllGlobalSignals();

    //  destroy all text editor instances

    destroyAllTextEditors();

    //  destroy all text formatting UI

    destroyAllTextFormatUi();

    //  destroy any one-time init stuff we've created

    destroyOneTimeSetup();
}
