import * as log from 'loglevel';
import {getActiveTextEditor} from "../../TextEditor";


/**
 * clear all user-defined styles from the editor
 */
export function clearStyles()
{
    let activeParagraph: HTMLParagraphElement;

    //  loop through all paragraph elements and reset their contents;
    //  we need to remove all extra styles and tags from the content as well

    activeParagraph = getActiveTextEditor().getActiveParagraph();

    //  reduce the paragraph's html content to its basic text content

    activeParagraph.innerHTML = activeParagraph.textContent;

    //  and remove all styles

    activeParagraph.removeAttribute('style');
}