import * as log from 'loglevel';

/**
 * set text colour
 * @param fontColour
 */
export function setTextColour(fontColour: string)
{
    document.execCommand('foreColor', false, fontColour);
}

/**
 * set background colour
 * @param backgroundColour
 */
export function setBackgroundColour(backgroundColour: string)
{
    document.execCommand('backColor', false, backgroundColour);
}


/**
 * extract font size from a variety of inputs
 * @param input
 * @returns {string}
 */
export function extractFontColour(input: string | number | Event) : string
{
    //  check the input we've been given;
    //  if might be a straight value, in which case we can use it directly,
    //  or an event from the UI, in which case we need to find the element's current value

    switch (typeof input)
    {
        case 'string':

            return <string>input;

        case 'object':

            return (<HTMLInputElement>(<Event>input).target).value;

        default:

            return 'black';
    }
}