import * as log from 'loglevel';

/**
 * set font-family
 * @param fontFamily
 */
export function setFontFamily(fontFamily: string)
{
    //  check we have a valid font-family; if not, stop here

    if ( ! fontFamily) return;

    //  set the font family for the selected text

    document.execCommand('fontName', false, fontFamily);
}


/**
 * extract font-family value from a variety of inputs
 * @param input
 * @returns {string}
 */
export function extractFontFamily(input: string | Event) : string
{
    //  check the input we've been given;
    //  if might be a straight value, in which case we can use it directly,
    //  or an event from the UI, in which case we need to find the element's current value

    switch (typeof input)
    {
        case 'string':

            return <string>input;

        case 'object':

            return (<HTMLInputElement>(<Event>input).target).value;
    }
}