import * as log from 'loglevel';
import {styleGroup} from "./SetStyle";
import {KWILL_CONSTS} from "../../../constants/Constants";
import {hasUnits} from "../utils/UnitsUtils";


/**
 * set line height for the selected text
 * @param lineHeight
 */
export function setLineHeight(lineHeight: string)
{
    styleGroup(KWILL_CONSTS.textFormats.LINE_HEIGHT, lineHeight);
}


/**
 * ensure the letter-spacing string is in a usable format
 * @param lineHeight
 */
function parseLineHeight(lineHeight: string) : string
{
    //  does the given string already include units? if so, let's use it

    if (hasUnits(lineHeight)) return lineHeight;

    //  if not, let's default to px

    return parseFloat(lineHeight) + 'px';
}