import * as log from 'loglevel';


/**
 * add or remove a link
 * @param url
 */
export function setLink(url: string)
{
    url ? document.execCommand('createLink', false, url) : document.execCommand('unlink');
}


/**
 * extract a URL from various inputs
 * @param input
 */
export function extractUrl(input: string | Event) : string
{
    //  check the input we've been given;
    //  if might be a straight value, in which case we can use it directly,
    //  or an event from the UI, in which case we need to find the element's current value

    if ( ! input) return null;

    switch (typeof input)
    {
        case 'string':

            return <string>input;

        case 'object':

            return (<HTMLInputElement>(<Event>input).target).value;

        default:

            return null;
    }
}
