/**
 * check whether a css string includes units
 * @param value
 * @returns {boolean}
 */
export function hasUnits(value: string) : boolean
{
    return !! value.match(/px|em|rem|%/);
}


/**
 * remove units from a css string
 * @param value
 * @returns {string}
 */
export function stripUnits(value: string) : string
{
    return parseFloat(value).toString();
}