import {getTextEditor} from "../editor/TextEditor";


/**
 * get the default content for an editor instance
 * @param editorID
 * @return {any}
 */
export function getDefaultContent(editorID: string) : string
{
    try
    {
        //  get the editor instance associated with an id and return the default content provided if it exists

        return getTextEditor(editorID).options.content.defaultHtml;
    }
    catch(e)
    {
        return null;
    }
}