/**
 * get the offset of an element on the page
 * @param element
 * @return {{top: number, left: number}}
 */
export function getElementOffset(element: HTMLElement) : { top: number, left: number }
{
    //  get the bounds of the embed element

    const bounds: ClientRect = element.getBoundingClientRect();

    //  calculate offset of element on the page

    return {
        top: bounds.top + window.pageYOffset,
        left: bounds.left + window.pageXOffset
    };
}