/**
 * remove a given attribute from all nodes in an array
 * @param nodes
 * @param attribute
 */
export function removeAttributes(nodes: Node[], attribute: string) : Node[]
{
    let result: Node[];

    //  filter out any nodes that lack the relevant attribute

    result = nodes.filter(node => hasAttribute(<HTMLElement>node, attribute));

    //  and remove the attribute

    result.map((node: HTMLElement) => node.removeAttribute(attribute));

    //  return the list of affected nodes

    return result;
}


/**
 * check whether a node has a given attribute
 * @param element
 * @param attribute
 * @returns {((name:string)=>(string|null))|string|null}
 */
function hasAttribute(element: HTMLElement, attribute: string) : boolean
{
    return !! (element.getAttribute && element.getAttribute(attribute));
}