import * as log from 'loglevel';
import {hasClass} from "../style/Css";

/**
 * deselect highlighted text when the user clicks outside the text pane
 * @param event
 * @returns {boolean}
 */
export function eventWasInternal(event: MouseEvent)
{
    //  when the user clicks within the document, check the target and parent of the click;
    //  if the click took place within a UI element associated with this kwill instance then ignore it

    let parent: HTMLElement = <HTMLElement>event.target;

    while (parent)
    {
        //  if the kwill id matches the current instance
        //  then we've clicked inside a UI element that's in use;
        //  stop here and don't de-select the text

        if (hasClass(parent, 'kwill')) return true;

        //  if it doesn't match, keep searching up the DOM

        parent = parent.parentElement;
    }

    return false;
}