import {forEach, set} from 'lodash';

export interface IStyle
{ 
    style: string, 
    value: any
}

/**
 * loop through all nodes and apply the given style properties
 * @param nodes
 * @param styles
 */
export function styleNodeArray(nodes: Node[], styles: IStyle[])
{
    forEach(nodes, (node: HTMLElement) =>
    {
        forEach(styles, (new_style: IStyle) => set(node, 'style.' + new_style.style, new_style.value));
    });
}